import React from "react";
import axios from "axios";
import SearchBar from "./SearchBar";
import ImageList from "./ImageList";
import GroupButton from "./GroupButton";
import Container from "@material-ui/core/Container";
import Grid from "@material-ui/core/Grid";
import { withStyles, Typography } from "@material-ui/core";
import { createStore } from "redux";
import { Provider } from "react-redux";

const styles = (theme) => ({
  root: {
    flexGrow: 1,
  },
  ContainerStyle: {
    marginTop: "20px",
  },
});

const initialState = {
  term: "",
  images: [],
};

function reducer(state = initialState, action) {
  switch (action.type) {
    case "SEARCH":
      // return {
      //   term: action.payload,
      // };
      return state.set("term", action.payload);
    case "IMG_ARRAY":
      return {
        images: action.payload,
      };
    default: {
      return state;
    }
  }
}

const store = createStore(
  reducer,
  window.__REDUX_DEVTOOLS_EXTENSION__ /
    window.__REDUX_DEVTOOLS_EXTENSION_COMPOSE__
);
// store.dispatch({ type: "ADD", payload: "Mountain" });

class App extends React.Component {
  constructor(props) {
    super(props);
  }

  state = { images: [] };

  onSearchSubmit = async (term) => {
    const res = await axios.get("https://api.unsplash.com/search/photos", {
      params: { query: term, per_page: 20 },
      headers: {
        Authorization: "Client-ID RxOfRHYrs4q27UpD2Fpa8HP-DmhQLqE8tdYIbcMsZLk",
      },
    });
    this.setState({ images: res.data.results });
    store.dispatch({ type: "IMG_ARRAY", payload: res.data.results });
    // console.log(store, "store");
  };

  componentDidMount() {
    this.onSearchSubmit("Mountain");
  }

  render() {
    const { classes } = this.props;
    return (
      <Provider store={store}>
        <div className={classes.root}>
          <Container maxWidth="md" className={classes.ContainerStyle}>
            {/* <div className="ui container" style={{ marginTop: "15px" }}> */}
            {/* <Grid container> */}
            <SearchBar onSubmit={this.onSearchSubmit} />
            {/* </Grid> */}
            <GroupButton onSubmit={this.onSearchSubmit} />
            <Typography variant="h2" glutterBottom>
              {console.log(store.getState(), "here")}
              {store.getState().term}
            </Typography>
            <ImageList images={this.state.images} />
            {/* <ImageList images={store.getState().images} /> */}
          </Container>
        </div>
      </Provider>
    );
  }
}

export default withStyles(styles)(App);
