//       return {
//         images: action.payload,
//       };
//     default: {
//       return state;
//     }
//   }
// }

// const store = createStore(
//   allReducers,
//   window.__REDUX_DEVTOOLS_EXTENSION__ && window.__REDUX_DEVTOOLS_EXTENSION__()
// );
// store.dispatch({ type: "ADD", payload: "Mountain" });

let searchTerm;

class App extends React.Component {
  constructor(props) {
    super(props);
  }

  // state = { images: [] };

  onSearchSubmit = async (term) => {
    const res = await axios.get("https://api.unsplash.com/search/photos", {
      params: { query: term, per_page: 20 },
      headers: {
        Authorization: "Client-ID RxOfRHYrs4q27UpD2Fpa8HP-DmhQLqE8tdYIbcMsZLk",
      },
    });
    // this.searchTerm = term;
    // this.setState({ images: res.data.results });
    // this.props.store.dispatch({ type: "IMG_ARRAY", payload: res.data.results });
    // this.props.dispatch({
    //   type: "IMG_ARRAY",
    //   payload: res.data.results,
    // });
    searchTerm = res.config.params.query;
    this.props.imageArray(res.data.results);
    // this.searchTerm = term;
    // console.log(store, "store");
  };

  componentDidMount() {
    this.onSearchSubmit("Mountain");
    // let searchTerm = "Mountain";
  }

  render() {
    const { classes } = this.props;
    console.log(this.props, "props");
    console.log(this.searchTerm, "SearchTerm");
    return (
      <div className={classes.root}>
        <Container maxWidth="md" className={classes.ContainerStyle}>
          {/* <div className="ui container" style={{ marginTop: "15px" }}> */}
          {/* <Grid container> */}
          <SearchBar onSubmit={this.onSearchSubmit} />
          {/* </Grid> */}
          <GroupButton onSubmit={this.onSearchSubmit} />
          <Typography variant="h2" glutterBottom>
            {/* {console.log(this.props.store.getState(), "here")} */}
            {/* {this.searchTerm} Images */}
            {this.searchTerm} Images
          </Typography>
          {console.log(this.props.images, "img")}
          {/* <ImageList images={this.state.images} /> */}
          <ImageList images={this.props.images} />
        </Container>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  term: state.term,
  images: state.images,
});

const mapDispatchToProps = (dispatch) => {
  return {
    imageArray: (data) => dispatch(imageArray(data)),
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(withStyles(styles)(App));
